package thread;

import java.io.BufferedWriter;
import java.io.File;
import java.io.FileWriter;
import java.math.BigInteger;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;

/**
 *
  * <p>  
 *  Javada thread kullanımı 
 * </p>  */ 

public class thread {

    /**
     * @param args the command line arguments
     */
    public static void main(String[] args) {
        
        //degerler girildi degistirilebilir.
        BigInteger sayi1=new BigInteger("");
        BigInteger sayi2=new BigInteger("3141592653589793238462643383279528841971693993751582974944592378164628628998628348253421176798214886513282366479384");
          
        String gecici = sayi2.toString(); //sayi2 stringe cevrildi
        int boyut=gecici.length();//uzunlugu boyuta atildi
        BigInteger[] sayi2dizi = new BigInteger[gecici.length()]; //string deger bigint diziye cevrildi
        
        for (int i = 0; i < gecici.length(); i++)
            {
             sayi2dizi[i]=new BigInteger(String.valueOf(gecici.charAt(i))); //diziye degerler atilir
            }
        int modu,bolum;       
        int havuzsayisi;
        long basla = System.nanoTime(); //hesaplama başlıyor 
        //havuzsayisi bulmak icin alinir
        bolum=boyut/10; 
        modu=boyut%10;
        if(modu==0){
             havuzsayisi=bolum; 
        }
        else
             havuzsayisi=bolum+1;
        
        Sonuc sonuc=new Sonuc(); //sonuc class i cagrildi
        ExecutorService havuz = Executors.newFixedThreadPool(havuzsayisi); //thread havuzu olusturuldu
       
      
        for(int i=0;i<boyut-modu;i=i+10){ //modu 0 olan degerler icin yani 10 un kati olan sayilar icin isleme yollama
           
            havuz.execute(new carpma(sayi1, gecici.substring(i, i+10),sonuc));
      
        }     
        if(modu!=0&&boyut>9){ //onun katindan geriye deger kaliyosa mod 0 degilse isleme yollama
           havuz.execute(new carpma(sayi1,gecici.substring(boyut-modu,boyut),sonuc)); 
        }
       
        if(boyut<=9){ //boyut 10 dan kucuk ise  tek thread tum sayi isleme yollanir
         
            havuz.execute(new carpma(sayi1, gecici,sonuc));
           
        }   
        havuz.shutdown(); //havuzu kapat

 while(!havuz.isTerminated()){ }

 long bit = System.nanoTime();//hesaplama bitiyor
 double sure = (bit-basla)/1000000.0;
 System.out.println("\nParalel Hesaplanma Süresi " + String.format("%.2f", sure) + " milisaniye.");
 long basla2 = System.nanoTime(); //hesaplama başlıyor
        Sonuc sonuc2=new Sonuc();
        ExecutorService havuz2 = Executors.newFixedThreadPool(1);//sari de tek thread de islem yapiliyormus gibi dusunulur.
        
        for(int i=0;i<sayi2dizi.length;i++){ //dizi elemanlari tek tek isleme yollanir
            havuz2.execute(new carpma(sayi1, sayi2dizi[i].toString(),sonuc2));
        }
        
        havuz2.shutdown();//havuzu kapat

 while(!havuz2.isTerminated()){ }

 long bit2 = System.nanoTime();//hesaplama bitiyor
 double sure2 = (bit2-basla2)/1000000.0;
 System.out.println("\nSeri Hesaplanma Süresi " + String.format("%.2f", sure2) + " milisaniye.");
    try{
                File dosya = new File("sonuc.txt");
                if(!dosya.exists()){ // eğer dosya yoksa
                    dosya.createNewFile(); //yeni olustur
                }
                  
                else                 
                {
                    dosya.delete(); // eğer dosyamız varsa.. // silme işlemi gerçekleştirir.
                }
                //yazma modunda
                FileWriter fw = new FileWriter(dosya,true);
                BufferedWriter bw = new BufferedWriter(fw);
                bw.write(sonuc.sonuc().toString()); //sonucu dosyaya yaz
                bw.close();
                System.out.println("Sonuç Dosyaya Yazıldı.");
        }
            catch(Exception e){
            System.out.println(e);
        }
    }
    
}
